const data = $input.all(); // Tu array original
const emailBody = $("Outlook").first().json.bodyPreview;

// Meses en espa√±ol
const meses = {
  enero: "01",
  febrero: "02",
  marzo: "03",
  abril: "04",
  mayo: "05",
  junio: "06",
  julio: "07",
  agosto: "08",
  septiembre: "09",
  octubre: "10",
  noviembre: "11",
  diciembre: "12",
};

// Extraer fecha
let fechaEntrega = "No encontrada";
const dateMatch = emailBody.match(
  /(\d{1,2})\s+de\s+(enero|febrero|marzo|abril|mayo|junio|julio|agosto|septiembre|octubre|noviembre|diciembre)/i
);
if (dateMatch) {
  const dia = dateMatch[1].padStart(2, "0");
  const mes = meses[dateMatch[2].toLowerCase()];
  const anio = "2025";
  fechaEntrega = `${dia}-${mes}-${anio}`;
}

// Extraer hora
let horaEntrega = "No encontrada";
const timeMatch = emailBody.match(/(\d{1,2}:\d{2}\s*(?:am|pm|AM|PM))/i);
if (timeMatch) {
  horaEntrega = timeMatch[1];
}

// Extraer representante
let representante = null;
const bodyClean = emailBody
  .replace(/\r?\n+/g, " ")
  .replace(/\s{2,}/g, " ")
  .replace(/saludos cordiales.*$/i, "")
  .replace(/atentamente.*$/i, "")
  .trim();

const repMatch = bodyClean.match(
  /(?:representante|responsable|persona de entrega)[^a-zA-Z0-9]{0,5}(?:es|:|-)?\s*([A-Z√Å√â√ç√ì√ö√ë][A-Za-z√Å√â√ç√ì√ö√ë\s.'-]+)/i
);

if (repMatch) {
  representante = repMatch[1]
    .replace(/\b(ADICIONAL|TOMAR|SECTOR|REFERENCIA|CONTACTO|ENTREGA|CELULAR|TEL√âFONO|EMAIL|CORREO).*$/i, "")
    .replace(/\b(Sr\.?|Sra\.?|Srta\.?|Ing\.?|Lic\.?|Dr\.?|Dra\.?)\b/gi, "")
    .replace(/\b(de|la|el|los|las)\b/gi, "")
    .replace(/[^A-Z√Å√â√ç√ì√ö√ëa-z√°√©√≠√≥√∫√±\s-]/g, "")
    .replace(/\s{2,}/g, " ")
    .trim()
    .toLowerCase()
    .replace(/\b[a-z√°√©√≠√≥√∫√±]/g, (l) => l.toUpperCase());
}

// Arrays
const guias = [];
const facturas = [];

// Recorrer JSON
for (const entry of data) {
  const items = entry.items || entry.json?.items || [];

  if (entry.tipo === "GuiaRemision" || entry.json?.tipo === "GuiaRemision") {
    items.forEach((item) => {
      const pdf = item.pdfText;
      if (!pdf) return;

      const matchFactura = pdf.match(/FACTURA\s*([\d-]+)/i);
      const matchRuc = pdf.match(/R\.U\.C\.\s*:\s*(\d{13})/i);
      const ruc = matchRuc ? matchRuc[1] : "No encontrado";

      const matchCliente = [...pdf.matchAll(/Raz√≥n Social \/ Nombres y Apellidos:\s*(.+)/g)];
      const nombreCliente =
        matchCliente.length > 1
          ? matchCliente[1][1].trim()
          : matchCliente[0]?.[1]?.trim() || "No encontrado";

      const matchDestino = pdf.match(/Destino \(Punto de llegada\):\s*(.+)/i);
      const direccionDestino = matchDestino ? matchDestino[1].trim() : "No encontrada";

      if (matchFactura) {
        guias.push({
          numeroFactura: matchFactura[1],
          texto: pdf,
          ruc,
          nombreCliente,
          direccionDestino,
          binary: item.binary?.data || null, // <-- guardamos el PDF
        });
      }
    });
  } else if (entry.tipo === "Factura" || entry.json?.tipo === "Factura") {
    items.forEach((item) => {
      const pdf = item.pdfText || item.json?.pdfText;
      if (!pdf) return;

      const matchFactura = pdf.match(/N¬∫\.\s*([\d-]+)/i);
      const matchRuc = pdf.match(/R\.U\.C\.\s*:\s*(\d{13})/i);
      const ruc = matchRuc ? matchRuc[1] : "No encontrado";

      if (matchFactura) {
        facturas.push({
          numeroFactura: matchFactura[1],
          texto: pdf,
          ruc,
          binary: item.binary?.data || null, // <-- guardamos el PDF
        });
      }
    });
  }
}

// Emparejar gu√≠a con factura
const resultadoFinal = guias.map((g) => {
  const factura = facturas.find((f) => f.numeroFactura === g.numeroFactura);

  return {
    ruc: g.ruc,
    nombre_cliente: g.nombreCliente,
    direccion_cliente: g.direccionDestino,
    factura: g.numeroFactura,
    fechaEntrega,
    horaEntrega,
    representante,
    emailFrom: $("Outlook").first().json.from,
    emailSubject: $("Outlook").first().json.subject,
    emailDate: $("Outlook").first().json.date,
    textoguia: g.texto,
    textofactura: factura ? factura.texto : "No encontrada",
    binario_pdf_guia: g.binary?.data || null,
    binario_pdf_factura: factura?.binary?.data || null,
  };
});

// Agregar conteo
resultadoFinal.push({
  guias: guias.length,
  facturas: facturas.length,
});

return resultadoFinal.map((r) => ({ json: r }));

const guiasRemision = [];
const facturas = [];

for (const item of $input.all()) {
  const pdfText = item.json.text || item.json.data || '';
  const filename = (item.json.pdfFilename || '').toLowerCase();
  const binary = item.binary || $input.item.binary || {}; // üîπ Usamos el binario original del PDF

  const textLower = pdfText.toLowerCase();

  const esGuia =
    textLower.includes('guia de remision') ||
    textLower.includes('g u √≠ a d e r e m i s i √≥ n') ||
    filename.includes('guia');

  const esFactura =
    textLower.includes('factura') ||
    textLower.includes('f a c t u r a') ||
    filename.includes('factura');

  if (esGuia) {
    guiasRemision.push({
      pdfText,
      pdfFilename: filename,
      binary // üîπ mantenemos el binario original
    });
  } else if (esFactura) {
    facturas.push({
      pdfText,
      pdfFilename: filename,
      binary // üîπ mantenemos el binario original
    });
  }
}

return [
  { json: { tipo: 'GuiaRemision', items: guiasRemision } },
  { json: { tipo: 'Factura', items: facturas } }
];

// Filtrar y separar solo los PDFs
const outputItems = [];
let pdfCounter = 1;

for (const item of $input.all()) {
  // Buscar en todas las propiedades binarias
  const binaryData = item.binary || {};

  for (const [key, value] of Object.entries(binaryData)) {
    const filename = value.fileName || value.filename || '';

    // Verificar si es un PDF
    if (filename.toLowerCase().endsWith('.pdf')) {
      outputItems.push({
        json: {
          ...item.json,
          pdfNumber: pdfCounter,
          pdfFilename: filename,
          binaryKey: key,
          emailBody: item.json.textPlain || item.json.textHtml || item.json.snippet || ''
        },
        binary: {
          data: value
        }
      });
      pdfCounter++;
    }
  }
}

return outputItems;

