DECLARE @horaEntregaRaw NVARCHAR(50) = '{{ $json["horaEntrega"] }}';
DECLARE @horaEntregaTime TIME;

-- ‚úÖ 1. Limpieza y conversi√≥n de hora
SET @horaEntregaRaw = LTRIM(RTRIM(LOWER(@horaEntregaRaw)));
SET @horaEntregaRaw = REPLACE(@horaEntregaRaw, '.', '');
SET @horaEntregaRaw = REPLACE(@horaEntregaRaw, ' a m', 'am');
SET @horaEntregaRaw = REPLACE(@horaEntregaRaw, ' p m', 'pm');
SET @horaEntregaRaw = REPLACE(@horaEntregaRaw, ' a. m.', 'am');
SET @horaEntregaRaw = REPLACE(@horaEntregaRaw, ' p. m.', 'pm');

SET @horaEntregaTime = TRY_CONVERT(TIME, @horaEntregaRaw, 0);

-- ‚ùå Si no se puede convertir, devolvemos "Durante el d√≠a"
IF @horaEntregaTime IS NULL
BEGIN
    SELECT TOP 1 
        id AS LISTA_HORAS_ID, 
        nombre AS LISTA_HORAS_NOMBRE
    FROM TBL_LISTA_HORAS
    WHERE nombre = 'Durante el d√≠a';
    RETURN;
END;

-- ‚úÖ 2. Convertimos la hora a formato num√©rico 1100, 1430, etc.
DECLARE @horaNum INT = DATEPART(HOUR, @horaEntregaTime) * 100 
                      + CASE WHEN DATEPART(MINUTE, @horaEntregaTime) >= 30 THEN 30 ELSE 0 END;

-- ‚úÖ 3. Si la hora est√° fuera del rango (6am a 5pm), devolvemos ‚ÄúDurante el d√≠a‚Äù
IF @horaNum < 600 OR @horaNum > 1700
BEGIN
    SELECT TOP 1 
        id AS LISTA_HORAS_ID, 
        nombre AS LISTA_HORAS_NOMBRE
    FROM TBL_LISTA_HORAS
    WHERE nombre = 'Durante el d√≠a';
    RETURN;
END;

-- ‚úÖ 4. Buscamos el bloque horario correcto
SELECT TOP 1 
    id AS LISTA_HORAS_ID, 
    nombre AS LISTA_HORAS_NOMBRE
FROM TBL_LISTA_HORAS
WHERE 
    nombre LIKE 'Hasta las%' 
    AND TRY_CAST(
        REPLACE(REPLACE(
            SUBSTRING(
                nombre,
                CASE WHEN CHARINDEX('Hasta las ', nombre) > 0 
                     THEN CHARINDEX('Hasta las ', nombre) + 10 
                     ELSE 0 END,
                8
            ), ':', ''), ' ', '') AS INT
    ) >= @horaNum
ORDER BY TRY_CAST(
        REPLACE(REPLACE(
            SUBSTRING(
                nombre,
                CASE WHEN CHARINDEX('Hasta las ', nombre) > 0 
                     THEN CHARINDEX('Hasta las ', nombre) + 10 
                     ELSE 0 END,
                8
            ), ':', ''), ' ', '') AS INT
    ) ASC;
